import React, { useState, useEffect } from "react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";

const zodiacSigns = [
  { name: "Aries", symbol: "â™ˆ" },
  { name: "Taurus", symbol: "â™‰" },
  { name: "Gemini", symbol: "â™Š" },
  { name: "Cancer", symbol: "â™‹" },
  { name: "Leo", symbol: "â™Œ" },
  { name: "Virgo", symbol: "â™" },
  { name: "Libra", symbol: "â™Ž" },
  { name: "Scorpio", symbol: "â™" },
  { name: "Sagittarius", symbol: "â™" },
  { name: "Capricorn", symbol: "â™‘" },
  { name: "Aquarius", symbol: "â™’" },
  { name: "Pisces", symbol: "â™“" },
];

const CalendarPage = () => {
  const [date, setDate] = useState(new Date());
  const [horoscopes, setHoroscopes] = useState({});

  useEffect(() => {
    const fetchHoroscope = async () => {
      let newHoroscopes = {};
      for (let sign of zodiacSigns) {
        const response = await fetch(
          `https://aztro.sameerkumar.website?sign=${sign.name.toLowerCase()}&day=today`,
          { method: "POST" }
        );
        const data = await response.json();
        newHoroscopes[sign.name] = data.description;
      }
      setHoroscopes(newHoroscopes);
    };
    fetchHoroscope();
  }, [date]);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-black text-white p-10">
      <h1 className="text-5xl font-bold text-yellow-400 mb-6">ðŸ“… Astrology Calendar</h1>
      <p className="text-lg max-w-3xl text-center mb-10 text-gray-300">
        Check important astrology events, moon phases, and daily horoscopes!
      </p>

      <div className="flex items-center justify-center w-full">
        {/* Zodiac Signs List (Left) */}
        <div className="w-1/4 hidden lg:flex flex-col space-y-4">
          {zodiacSigns.slice(0, 6).map((sign) => (
            <div key={sign.name} className="p-3 bg-white/10 rounded-lg text-center shadow-md hover:scale-105 transition">
              <h3 className="text-xl font-bold">{sign.symbol} {sign.name}</h3>
              <p className="text-gray-400 text-sm">{horoscopes[sign.name] || "Loading..."}</p>
            </div>
          ))}
        </div>

        {/* Calendar in the Center */}
        <div className="bg-white/10 backdrop-blur-lg p-6 rounded-lg shadow-lg shadow-yellow-500/30">
          <Calendar 
            onChange={setDate} 
            value={date} 
            className="border-none text-black rounded-lg"
          />
          <p className="mt-4 text-lg text-gray-300">Selected Date: <span className="text-yellow-400">{date.toDateString()}</span></p>
        </div>

        {/* Zodiac Signs List (Right) */}
        <div className="w-1/4 hidden lg:flex flex-col space-y-4">
          {zodiacSigns.slice(6, 12).map((sign) => (
            <div key={sign.name} className="p-3 bg-white/10 rounded-lg text-center shadow-md hover:scale-105 transition">
              <h3 className="text-xl font-bold">{sign.symbol} {sign.name}</h3>
              <p className="text-gray-400 text-sm">{horoscopes[sign.name] || "Loading..."}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CalendarPage;
