import React, { useState } from "react";

const Customers = () => {
  const [testimonials, setTestimonials] = useState([
    { name: "John Doe", review: "Great astrology service! Highly recommended!" },
    { name: "Alice Smith", review: "I love the accurate horoscope readings!" },
    { name: "David Brown", review: "The best astrology consultation I have ever had!" },
    { name: "Sophia Johnson", review: "Superb customer support and expert advice!" },
    { name: "Michael Lee", review: "I enjoyed the tarot reading experience!" },
  ]);

  const [newReview, setNewReview] = useState("");
  const [newName, setNewName] = useState("");

  // âœ… Add New Review
  const handleAddReview = () => {
    if (newName.trim() === "" || newReview.trim() === "") return;
    setTestimonials([...testimonials, { name: newName, review: newReview }]);
    setNewName("");
    setNewReview("");
  };

  return (
    <div className="py-12 bg-gray-900 text-white">
      <h2 className="text-3xl font-bold text-center mb-8">ðŸŒŸ Our Customers ðŸŒŸ</h2>

      {/* âœ… Scrollable Testimonials with Hidden Scrollbar */}
      <div className="flex space-x-6 overflow-x-auto px-6 custom-scrollbar">
        {testimonials.map((customer, index) => (
          <div key={index} className="min-w-[250px] bg-gray-800 p-6 rounded-lg shadow-lg">
            <h3 className="text-xl font-semibold">{customer.name}</h3>
            <p className="text-gray-300 mt-2">{customer.review}</p>
          </div>
        ))}
      </div>

      {/* âœ… Write a Review Form */}
      <div className="mt-8 text-center">
        <h3 className="text-2xl font-semibold text-yellow-400">ðŸ’¬ Write a Review</h3>
        <input
          type="text"
          placeholder="Your Name"
          value={newName}
          onChange={(e) => setNewName(e.target.value)}
          className="mt-4 p-3 w-80 bg-gray-800 text-white rounded-md border border-gray-700 outline-none"
        />
        <textarea
          placeholder="Your Review"
          value={newReview}
          onChange={(e) => setNewReview(e.target.value)}
          className="mt-4 p-3 w-80 h-24 bg-gray-800 text-white rounded-md border border-gray-700 outline-none"
        />
        <br />
        <button
          onClick={handleAddReview}
          className="mt-4 px-6 py-3 bg-gradient-to-r from-yellow-500 to-orange-500 text-black font-semibold rounded-full 
          shadow-lg hover:shadow-yellow-500/50 hover:scale-105 transition-all duration-300"
        >
          Submit Review
        </button>
      </div>
    </div>
  );
};

export default Customers;
