import React, { useState } from "react";

const servicesData = [
  { id: 1, name: "Daily Horoscope", category: "Horoscope" },
  { id: 2, name: "Weekly Horoscope", category: "Horoscope" },
  { id: 3, name: "Palmistry", category: "Readings" },
  { id: 4, name: "Tarot Reading", category: "Readings" },
  { id: 5, name: "Numerology", category: "Readings" },
  { id: 6, name: "Chat with Astrologer", category: "Consultation" },
  { id: 7, name: "Talk to Astrologer", category: "Consultation" },
  { id: 8, name: "Kundli Matching", category: "Consultation" },
];

const categories = ["All", "Horoscope", "Readings", "Consultation"];

const Services = () => {
  const [selectedCategory, setSelectedCategory] = useState("All");

  const filteredServices = servicesData.filter(
    (service) => selectedCategory === "All" || service.category === selectedCategory
  );

  return (
    <div className="py-24 bg-gradient-to-br from-black via-gray-900 to-black text-white text-center relative overflow-hidden">
      
      {/* Glowing Background Effects */}
      <div className="absolute top-0 left-1/3 w-[500px] h-[500px] bg-purple-500 opacity-20 rounded-full blur-[200px]"></div>
      <div className="absolute bottom-0 right-1/3 w-[500px] h-[500px] bg-yellow-500 opacity-20 rounded-full blur-[200px]"></div>

      {/* Section Title */}
      <h2 className="text-5xl font-extrabold text-yellow-400 mb-12 tracking-wide">
        ðŸ”® Explore Our Astrology Services
      </h2>
      <p className="text-gray-400 max-w-4xl mx-auto text-lg leading-relaxed mb-14">
        Discover expert astrology services tailored to your needs. Choose a category and explore!
      </p>

      {/* Category Filter */}
      <div className="flex flex-wrap justify-center gap-6 mb-16">
        {categories.map((category) => (
          <button
            key={category}
            className={`px-6 py-3 rounded-full text-lg font-semibold transition-all duration-300 
              ${
                selectedCategory === category
                  ? "bg-yellow-500 text-black shadow-md shadow-yellow-500/50 scale-105"
                  : "bg-gray-800 text-gray-300 hover:bg-yellow-500 hover:text-black hover:scale-105"
              }`}
            onClick={() => setSelectedCategory(category)}
          >
            {category}
          </button>
        ))}
      </div>

      {/* Services Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-12 px-8">
        {filteredServices.map((service) => (
          <div
            key={service.id}
            className="relative w-[300px] p-8 bg-white/10 backdrop-blur-md rounded-xl shadow-lg border border-gray-700 
            transition-all duration-300 transform hover:scale-105 hover:shadow-yellow-500/50"
          >
            <h3 className="text-xl font-semibold text-yellow-400">{service.name}</h3>
            <p className="text-gray-300 mt-4 text-lg leading-relaxed">{service.category}</p>
            <button className="mt-6 px-6 py-3 bg-gradient-to-r from-yellow-500 to-orange-500 text-black 
            font-semibold rounded-full hover:scale-105 transition-all duration-300 hover:shadow-yellow-500/50">
              Explore â†’
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Services;
