import React, { useState } from "react";
import { Link } from "react-router-dom";
import { FaBars, FaTimes, FaGlobe, FaCalendarAlt } from "react-icons/fa";

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [horoscopeDropdown, setHoroscopeDropdown] = useState(false);

  return (
    <nav className="fixed top-0 left-0 w-full z-50 bg-black/60 backdrop-blur-md border-b border-gray-700 shadow-lg shadow-yellow-500/10 transition-all duration-500">
      <div className="max-w-7xl mx-auto px-8 flex items-center justify-between py-4">

        {/* üîÆ Logo */}
        <Link to="/" className="text-3xl font-extrabold text-yellow-400 hover:scale-110 transition">
          üîÆ AstroSphere
        </Link>

        {/* üåü Desktop Menu */}
        <ul className="hidden lg:flex space-x-8 text-gray-300 font-medium text-lg">
          <li><Link to="/" className="hover:text-yellow-400 transition">üè† Home</Link></li>
          <li><Link to="/services" className="hover:text-yellow-400 transition">‚ú® Services</Link></li>
          <li className="relative">
            <button onClick={() => setHoroscopeDropdown(!horoscopeDropdown)} className="hover:text-yellow-400 transition flex items-center">
              <FaGlobe className="mr-2" /> Horoscopes ‚ñº
            </button>
            {horoscopeDropdown && (
              <ul className="absolute left-0 mt-2 bg-black/50 backdrop-blur-xl shadow-lg rounded-lg p-2 w-52 border border-gray-700 transition-all">
                <li><Link to="/horoscope" className="block px-4 py-2 hover:bg-gray-800 rounded-md">Daily Horoscope</Link></li>
                <li><Link to="/horoscope?weekly" className="block px-4 py-2 hover:bg-gray-800 rounded-md">Weekly Horoscope</Link></li>
              </ul>
            )}
          </li>
          <li><Link to="/calendar" className="hover:text-yellow-400 transition">üìÖ Calendar</Link></li>
          <li><Link to="/blog" className="hover:text-yellow-400 transition">üì∞ Blog</Link></li>
        </ul>

        {/* üì± Mobile Menu Button */}
        <button className="lg:hidden text-gray-400 text-2xl" onClick={() => setMenuOpen(!menuOpen)}>
          {menuOpen ? <FaTimes /> : <FaBars />}
        </button>
      </div>

      {/* üì± Mobile Menu */}
      {menuOpen && (
        <div className="lg:hidden fixed top-0 left-0 w-2/3 h-full bg-black/90 shadow-lg z-50 transition-all">
          <button className="absolute top-4 right-4 text-gray-400 text-3xl" onClick={() => setMenuOpen(false)}>
            <FaTimes />
          </button>
          <ul className="text-center mt-20 space-y-6 text-gray-300 text-lg">
            <li><Link to="/" className="block py-2 hover:text-yellow-400">üè† Home</Link></li>
            <li><Link to="/services" className="block py-2 hover:text-yellow-400">‚ú® Services</Link></li>
            <li><Link to="/horoscope" className="block py-2 hover:text-yellow-400">üåô Horoscope</Link></li>
            <li><Link to="/calendar" className="block py-2 hover:text-yellow-400">üìÖ Calendar</Link></li>
            <li><Link to="/blog" className="block py-2 hover:text-yellow-400">üì∞ Blog</Link></li>
          </ul>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
