import React from "react";
import { FaComments, FaShoppingCart, FaHandsHelping, FaStar } from "react-icons/fa";

const services = [
  { title: "Chat with Astrologer", icon: <FaComments />, color: "bg-pink-500" },
  { title: "Talk to Astrologer", icon: <FaHandsHelping />, color: "bg-blue-500" },
  { title: "AstroMall Shop", icon: <FaShoppingCart />, color: "bg-green-500" },
  { title: "Book a Pooja", icon: <FaStar />, color: "bg-orange-500" },
];

const Services = () => {
  return (
    <div className="py-16 bg-gray-100 dark:bg-gray-900 text-center">
      <h2 className="text-4xl font-bold text-gray-800 dark:text-white mb-6">âœ¨ Our Astrology Services</h2>
      <div className="flex flex-wrap justify-center gap-6">
        {services.map((service, index) => (
          <div key={index} className={`w-48 h-48 flex flex-col items-center justify-center text-white rounded-lg shadow-lg ${service.color} hover:scale-105 transform transition`}>
            <div className="text-5xl">{service.icon}</div>
            <p className="mt-4 font-semibold">{service.title}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Services;
