const Blog = () => {
  return (
    <div className="py-16 bg-white dark:bg-gray-900">
      <h2 className="text-4xl font-bold text-center text-gray-800 dark:text-white mb-6">
        ðŸ“– Latest From Blog
      </h2>

      <div className="flex flex-wrap justify-center gap-6">
        {[1, 2, 3].map((_, index) => (
          <div key={index} className="w-80 bg-gray-100 dark:bg-gray-800 rounded-lg shadow-lg overflow-hidden transform hover:scale-105 transition">
            <img src={`https://source.unsplash.com/400x250/?astrology,stars&random=${index}`} alt="Blog" />
            <div className="p-4">
              <h3 className="text-lg font-bold">Astrology Insights #{index + 1}</h3>
              <p className="text-gray-600 dark:text-gray-300 mt-2">Discover the latest astrological predictions.</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Blog;
