import React from "react";
import { useNavigate } from "react-router-dom";

const astrologersData = [
  { id: 1, name: "Rachna", expertise: "Numerology", experience: 10, image: "/images/rachna.jpg" },
  { id: 2, name: "Pramod", expertise: "Numerology", experience: 8, image: "/images/pramod.jpg" },
  { id: 3, name: "Sangeetha", expertise: "Vedic", experience: 15, image: "/images/sangeetha.jpg" },
  { id: 4, name: "Amit", expertise: "Vedic Astrology", experience: 12, image: "/images/amit.jpg" },
];

const Hero = () => {
  const navigate = useNavigate();

  return (
    <div className="relative flex flex-col items-center justify-center text-center bg-black min-h-screen px-6">
      {/* Glowing Background Effects */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute top-1/4 left-1/3 w-[400px] h-[400px] bg-yellow-500 opacity-20 rounded-full blur-[120px]"></div>
        <div className="absolute bottom-1/4 right-1/3 w-[400px] h-[400px] bg-purple-500 opacity-20 rounded-full blur-[120px]"></div>
      </div>

      {/* Main Content */}
      <h1 className="text-6xl md:text-7xl font-extrabold text-white animate-fadeIn tracking-wide">
        ðŸ”® Unlock Your <span className="text-transparent bg-clip-text bg-gradient-to-r from-yellow-400 to-pink-500">Destiny</span>
      </h1>
      <p className="mt-4 text-lg text-gray-300 animate-slideUp">Get personalized astrology guidance today.</p>

      {/* Call to Action Buttons */}
      <div className="mt-6 flex space-x-6">
        <button className="px-8 py-3 bg-gradient-to-r from-yellow-500 to-orange-500 text-black font-semibold rounded-full shadow-lg hover:scale-105 transition-transform duration-300 hover:shadow-yellow-500/50">
          Get Started
        </button>
        <button className="px-8 py-3 border border-yellow-500 text-yellow-400 font-semibold rounded-full shadow-lg hover:bg-yellow-500 hover:text-black hover:scale-105 transition-transform duration-300">
          Learn More
        </button>
      </div>

      {/* Astrologers Section with 3D Cards */}
      <div className="mt-16 w-full max-w-6xl px-6">
        <h2 className="text-4xl font-bold text-yellow-400">Our Top Astrologers</h2>
        <p className="text-gray-400">13000+ Best Astrologers from India for Online Consultation</p>

        {/* Centered Astrologers List */}
        <div className="flex justify-center gap-6 mt-6">
          {astrologersData.map((astro) => (
            <div
              key={astro.id}
              className="bg-white/10 backdrop-blur-xl text-white p-6 rounded-xl shadow-xl text-center w-56 
              transform transition-all duration-300 hover:scale-105 hover:shadow-yellow-500/50"
            >
              <img
                src={astro.image}
                alt={astro.name}
                className="rounded-full mx-auto w-24 h-24 mb-3 border-2 border-yellow-400 shadow-md hover:shadow-yellow-500/50"
              />
              <h3 className="text-lg font-semibold">{astro.name}</h3>
              <p className="text-gray-300">{astro.expertise}</p>
              <p className="text-gray-400">{astro.experience} years experience</p>
            </div>
          ))}
        </div>

        {/* View All Astrologers Button */}
        <button
          onClick={() => navigate("/astrologers")}
          className="mt-8 px-6 py-3 bg-gradient-to-r from-yellow-500 to-orange-500 text-black font-semibold rounded-full shadow-lg hover:scale-105 transition-transform duration-300 hover:shadow-yellow-500/50"
        >
          View All Astrologers
        </button>
      </div>
    </div>
  );
};

export default Hero;
