import React from "react";

const Blog = () => {
  return (
    <div className="py-16 bg-black text-white relative overflow-hidden">
      {/* Glowing Background Elements */}
      <div className="absolute top-0 left-1/3 w-96 h-96 bg-purple-500 opacity-20 rounded-full blur-[120px]"></div>
      <div className="absolute bottom-0 right-1/3 w-96 h-96 bg-yellow-500 opacity-20 rounded-full blur-[120px]"></div>

      <h2 className="text-4xl font-bold text-center text-yellow-400 mb-12 tracking-wide">
        ðŸ“– Latest From The Cosmic Blog
      </h2>

      {/* Blog Cards */}
      <div className="flex flex-wrap justify-center gap-8">
        {[1, 2, 3].map((_, index) => (
          <div 
            key={index} 
            className="relative w-80 bg-white/10 backdrop-blur-lg rounded-xl shadow-lg border border-yellow-500/30 
            transform transition-all duration-300 hover:scale-105 hover:shadow-yellow-500/50"
          >
            <div className="overflow-hidden rounded-t-xl">
              <img 
                src={`https://source.unsplash.com/400x250/?astrology,stars,galaxy&random=${index}`} 
                alt="Blog" 
                className="rounded-t-xl transition-transform duration-300 hover:scale-110"
              />
            </div>
            <div className="p-6 text-center">
              <h3 className="text-xl font-bold text-yellow-400">Astrology Insights #{index + 1}</h3>
              <p className="text-gray-300 mt-3">Explore the mysteries of the universe with our latest cosmic insights.</p>
              <button className="mt-4 px-6 py-2 bg-gradient-to-r from-yellow-500 to-orange-500 text-black 
              font-semibold rounded-full hover:scale-105 transition-transform duration-300 hover:shadow-yellow-500/50">
                Read More â†’
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Blog;
