import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { FaBars, FaTimes, FaGlobe, FaCalendarAlt, FaSun, FaMoon } from "react-icons/fa";

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [horoscopeDropdown, setHoroscopeDropdown] = useState(false);
  const [darkMode, setDarkMode] = useState(
    localStorage.getItem("theme") === "dark"
  );

  // âœ… Apply or Remove Dark Mode
  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add("dark"); // âœ… Add Dark Mode
      localStorage.setItem("theme", "dark");
    } else {
      document.documentElement.classList.remove("dark"); // âœ… Remove Dark Mode
      localStorage.setItem("theme", "light");
    }
    console.log("Dark Mode:", darkMode); // Debugging
  }, [darkMode]);

  return (
    <nav className="fixed top-0 left-0 w-full z-50 bg-gray-900 dark:bg-black/80 backdrop-blur-md border-b border-gray-700 shadow-lg transition-all duration-500">
      <div className="max-w-7xl mx-auto px-8 flex items-center justify-between py-4">

        {/* ðŸ”® Logo */}
        <Link to="/" className="text-3xl font-extrabold text-yellow-400">
          ðŸ”® AstroSphere
        </Link>

        {/* ðŸŒ— Dark Mode Toggle */}
        <button
          onClick={() => setDarkMode(!darkMode)}
          className="flex items-center px-4 py-2 bg-gray-700 dark:bg-gray-100 text-white dark:text-black rounded-full shadow-md hover:scale-110 transition"
        >
          {darkMode ? <FaSun className="text-yellow-400" /> : <FaMoon />}
          <span className="ml-2">{darkMode ? "Light Mode" : "Dark Mode"}</span>
        </button>

        {/* ðŸ“± Mobile Menu Button */}
        <button className="lg:hidden text-gray-400 text-2xl" onClick={() => setMenuOpen(!menuOpen)}>
          {menuOpen ? <FaTimes /> : <FaBars />}
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
