import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const Navbar = () => {
  const [darkMode, setDarkMode] = useState(
    localStorage.getItem("darkMode") === "true"
  );

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
    localStorage.setItem("darkMode", darkMode);
  }, [darkMode]);

  return (
    <nav className="bg-gray-900 shadow-md fixed top-0 left-0 w-full z-50">
      <div className="max-w-7xl mx-auto px-6 flex justify-between items-center py-4">
        {/* Logo */}
        <Link to="/" className="text-2xl font-bold text-yellow-500">
          Astrology Hub
        </Link>

        {/* Desktop Menu */}
        <ul className="hidden md:flex space-x-6 text-white font-medium">
          <li><Link to="/" className="hover:text-yellow-400 transition">Home</Link></li>
          <li><Link to="/horoscope" className="hover:text-yellow-400 transition">Horoscope</Link></li>
          <li><Link to="/consult" className="hover:text-yellow-400 transition">Consult</Link></li>
          <li><Link to="/tarot-card" className="hover:text-yellow-400 transition">Tarot Card</Link></li>
        </ul>

        {/* Dark Mode Toggle */}
        <button
          onClick={() => setDarkMode(!darkMode)}
          className="text-white text-lg"
        >
          {darkMode ? "ðŸŒž" : "ðŸŒ™"}
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
