import React, { useState } from "react";
import { motion } from "framer-motion";
import zodiacWheel from "../assets/zodiac-wheel.png"; // Add your zodiac wheel image

const zodiacSigns = [
  { name: "Aries", dates: "Mar 21 - Apr 19" },
  { name: "Taurus", dates: "Apr 20 - May 20" },
  { name: "Gemini", dates: "May 21 - Jun 20" },
  { name: "Cancer", dates: "Jun 21 - Jul 22" },
  { name: "Leo", dates: "Jul 23 - Aug 22" },
  { name: "Virgo", dates: "Aug 23 - Sep 22" },
  { name: "Libra", dates: "Sep 23 - Oct 22" },
  { name: "Scorpio", dates: "Oct 23 - Nov 21" },
  { name: "Sagittarius", dates: "Nov 22 - Dec 21" },
  { name: "Capricorn", dates: "Dec 22 - Jan 19" },
  { name: "Aquarius", dates: "Jan 20 - Feb 18" },
  { name: "Pisces", dates: "Feb 19 - Mar 20" },
];

const ZodiacWheel = () => {
  const [selectedSign, setSelectedSign] = useState(null);

  return (
    <div className="py-16 bg-black text-white text-center relative">
      <h2 className="text-4xl font-extrabold text-yellow-400 mb-6">âœ¨ Zodiac Horoscope</h2>
      <p className="text-gray-400 text-lg mb-8">Tap on your zodiac sign to see your horoscope.</p>

      {/* Zodiac Wheel Image */}
      <div className="relative flex justify-center">
        <motion.img
          src={zodiacWheel}
          alt="Zodiac Wheel"
          className="w-96 md:w-[450px] lg:w-[500px] cursor-pointer transition-transform duration-500 hover:scale-105"
        />
      </div>

      {/* Zodiac Buttons */}
      <div className="flex flex-wrap justify-center gap-4 mt-8">
        {zodiacSigns.map((sign, index) => (
          <motion.button
            key={index}
            whileHover={{ scale: 1.1 }}
            className="px-6 py-2 bg-gray-800 text-yellow-400 font-semibold rounded-full border border-yellow-500 shadow-lg
              hover:bg-yellow-500 hover:text-black transition-all duration-300"
            onClick={() => setSelectedSign(sign)}
          >
            {sign.name}
          </motion.button>
        ))}
      </div>

      {/* Horoscope Popup */}
      {selectedSign && (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="mt-8 bg-gray-900 text-white p-6 rounded-lg shadow-lg border border-yellow-500"
        >
          <h3 className="text-2xl font-bold text-yellow-400">{selectedSign.name} â™ˆ</h3>
          <p className="text-gray-300 mt-2">{selectedSign.dates}</p>
          <p className="text-gray-400 mt-4">ðŸ”® Your daily horoscope will appear here!</p>
          <button
            className="mt-4 px-6 py-2 bg-yellow-500 text-black font-semibold rounded-full hover:bg-yellow-600 transition duration-300"
            onClick={() => setSelectedSign(null)}
          >
            Close
          </button>
        </motion.div>
      )}
    </div>
  );
};

export default ZodiacWheel;
