import React from "react";
import { FaStar, FaHandSparkles, FaBookOpen, FaGem, FaChartLine, FaComments, FaUserAstronaut, FaHeart, FaSun, FaGlobe, FaSearch, FaUser, FaBalanceScale } from "react-icons/fa";

const services = [
  { name: "Daily Horoscope", icon: <FaSun />, link: "/horoscope" },
  { name: "Weekly Horoscope", icon: <FaGlobe />, link: "/horoscope?weekly" },
  { name: "Palmistry", icon: <FaHandSparkles />, link: "/palmistry" },
  { name: "Tarot Reading", icon: <FaStar />, link: "/tarot-card" },
  { name: "Numerology", icon: <FaChartLine />, link: "/numerology" },
  { name: "Chat with Astrologer", icon: <FaComments />, link: "/chat-astrologer" },
  { name: "Talk to Astrologer", icon: <FaUserAstronaut />, link: "/talk-astrologer" },
  { name: "Kundli Matching", icon: <FaHeart />, link: "/kundli-matching" },
  { name: "Gemstone Consultation", icon: <FaGem />, link: "/gem-store" },
  { name: "Vastu Consultation", icon: <FaBalanceScale />, link: "/vastu-consult" },
  { name: "Book a Pooja", icon: <FaBookOpen />, link: "/book-pooja" },
];

const AstrologyServices = () => {
  return (
    <section className="py-16 bg-gradient-to-b from-gray-900 to-black text-white">
      <div className="max-w-7xl mx-auto text-center">
        <h2 className="text-4xl font-bold text-yellow-400">ðŸ”® Our Astrology Services</h2>
        <p className="mt-4 text-lg text-gray-400">Explore a variety of astrology services tailored for you.</p>
      </div>

      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 max-w-7xl mx-auto mt-10 px-6">
        {services.map((service, index) => (
          <a 
            key={index} 
            href={service.link} 
            className="group bg-white/10 dark:bg-gray-800/50 rounded-lg p-6 text-center transition transform hover:scale-105 shadow-lg backdrop-blur-md border border-white/20 dark:border-gray-700 hover:bg-white/20 hover:shadow-xl"
          >
            <div className="text-5xl text-yellow-400 group-hover:text-yellow-300 transition">
              {service.icon}
            </div>
            <h3 className="mt-4 text-lg font-semibold">{service.name}</h3>
          </a>
        ))}
      </div>
    </section>
  );
};

export default AstrologyServices;
