import React from "react";
import { Link } from "react-router-dom"; // ‚úÖ Import for navigation

const Customers = () => {
  const testimonials = [
    { name: "John Doe", review: "üåü Amazing astrology service! Highly recommended!" },
    { name: "Alice Smith", review: "‚ú® I love the accurate horoscope readings!" },
    { name: "David Brown", review: "üîÆ The best astrology consultation I have ever had!" },
    { name: "Sophia Johnson", review: "üí´ Superb customer support and expert advice!" },
    { name: "Michael Lee", review: "üî• I enjoyed the tarot reading experience!" },
  ];

  return (
    <div className="py-12 bg-black text-white text-center">
      {/* Title */}
      <h2 className="text-3xl font-extrabold text-yellow-400 mb-8">
        üåü What Our Customers Say
      </h2>

      {/* Scrollable Testimonials Container */}
      <div className="flex space-x-6 overflow-x-auto px-6 scrollbar-hide justify-center">
        {testimonials.map((customer, index) => (
          <div
            key={index}
            className="min-w-[300px] bg-gray-900/40 backdrop-blur-lg p-6 rounded-lg shadow-lg border border-gray-700 
            transition-all duration-300 hover:shadow-yellow-500/30 text-left"
          >
            <h3 className="text-xl font-semibold text-yellow-400">{customer.name}</h3>
            <p className="text-gray-300 mt-2">{customer.review}</p>
          </div>
        ))}
      </div>

      {/* Link to Review Page */}
      <Link to="/review">
        <button
          className="mt-8 px-6 py-3 bg-gradient-to-r from-yellow-500 to-orange-500 text-black 
          font-bold text-lg uppercase rounded-full shadow-lg hover:shadow-yellow-500/50 hover:scale-105 
          transition-all duration-300"
        >
          ‚úçÔ∏è Write a Review
        </button>
      </Link>
    </div>
  );
};

export default Customers;
