import React from "react";

const Hero = () => {
  return (
    <div className="relative h-screen flex flex-col items-center justify-center text-center bg-gradient-to-b from-black via-gray-900 to-gray-800 text-white">
      {/* Animated Background Glow */}
      <div className="absolute inset-0">
        <div className="absolute top-1/4 left-1/3 w-96 h-96 bg-yellow-500 opacity-10 rounded-full blur-3xl"></div>
        <div className="absolute bottom-1/4 right-1/3 w-96 h-96 bg-purple-500 opacity-10 rounded-full blur-3xl"></div>
      </div>

      {/* Main Content */}
      <h1 className="text-5xl md:text-6xl font-extrabold animate-fadeIn">
        Unlock Your <span className="text-yellow-400">Destiny</span>
      </h1>
      <p className="mt-4 text-lg animate-slideUp">Get personalized astrology guidance today.</p>

      {/* Button with hover effects */}
      <button className="mt-6 px-6 py-3 bg-yellow-500 text-black rounded-full hover:bg-yellow-600 transition transform hover:scale-105">
        Get Started
      </button>
    </div>
  );
};

export default Hero;
