import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { FaBars, FaTimes, FaGlobe, FaCalendarAlt, FaSun, FaMoon, FaUserAstronaut } from "react-icons/fa";

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [horoscopeDropdown, setHoroscopeDropdown] = useState(false);
  const [darkMode, setDarkMode] = useState(localStorage.getItem("theme") === "dark");

  // ‚úÖ Apply Dark Mode on Load
  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add("dark");
      localStorage.setItem("theme", "dark");
    } else {
      document.documentElement.classList.remove("dark");
      localStorage.setItem("theme", "light");
    }
  }, [darkMode]);

  return (
    <nav className="fixed top-0 left-0 w-full z-50 backdrop-blur-lg bg-black/50 dark:bg-gray-900 border-b border-gray-700 shadow-lg transition-all duration-500">
      <div className="max-w-7xl mx-auto px-8 flex items-center justify-between py-4">

        {/* üîÆ Logo with Glow */}
        <Link to="/" className="text-3xl font-extrabold text-yellow-400 hover:scale-110 transition">
          üîÆ AstroSphere
        </Link>

        {/* üåü Desktop Menu */}
        <ul className="hidden lg:flex space-x-8 text-gray-300 dark:text-white font-medium text-lg">
          <li><Link to="/" className="hover:text-yellow-400 transition-all duration-300">üè† Home</Link></li>
          <li><Link to="/services" className="hover:text-yellow-400 transition-all duration-300">‚ú® Services</Link></li>

          {/* Horoscope Dropdown */}
          <li className="relative group">
            <button 
              onClick={() => setHoroscopeDropdown(!horoscopeDropdown)} 
              className="hover:text-yellow-400 transition-all duration-300 flex items-center"
            >
              <FaGlobe className="mr-2" /> Horoscopes ‚ñº
            </button>
            {horoscopeDropdown && (
              <ul className="absolute left-0 mt-2 bg-black/50 dark:bg-gray-800 backdrop-blur-xl shadow-lg rounded-lg p-2 w-52 border border-gray-700 transition-all duration-300">
                <li><Link to="/horoscope" className="block px-4 py-2 hover:bg-gray-800 rounded-md">Daily Horoscope</Link></li>
                <li><Link to="/horoscope?weekly" className="block px-4 py-2 hover:bg-gray-800 rounded-md">Weekly Horoscope</Link></li>
              </ul>
            )}
          </li>

          <li><Link to="/calendar" className="hover:text-yellow-400 transition-all duration-300">üìÖ Calendar</Link></li>
          <li><Link to="/blog" className="hover:text-yellow-400 transition-all duration-300">üì∞ Blog</Link></li>

          {/* üîÆ Match Kundli Button (Professional) */}
          <li>
            <Link to="/kundli-match">
              <button className="px-6 py-2 bg-gradient-to-r from-yellow-500 to-orange-500 text-black font-bold text-lg rounded-full shadow-lg hover:scale-110 transition-all duration-300 hover:shadow-yellow-500/50">
                üîÆ Match Kundli
              </button>
            </Link>
          </li>
        </ul>

        {/* üåó Dark Mode Toggle Button */}
        <button
          onClick={() => setDarkMode(!darkMode)}
          className="hidden lg:flex items-center px-4 py-2 bg-gray-800 dark:bg-gray-100 text-white dark:text-black rounded-full shadow-md hover:scale-110 transition-all duration-300"
        >
          {darkMode ? <FaSun className="text-yellow-400" /> : <FaMoon />}
          <span className="ml-2">{darkMode ? "Light Mode" : "Dark Mode"}</span>
        </button>

        {/* üì± Mobile Menu Button */}
        <button className="lg:hidden text-gray-400 text-2xl" onClick={() => setMenuOpen(!menuOpen)}>
          {menuOpen ? <FaTimes /> : <FaBars />}
        </button>
      </div>

      {/* üì± Mobile Menu */}
      {menuOpen && (
        <div className="lg:hidden fixed top-0 left-0 w-2/3 h-full bg-black/90 dark:bg-gray-900 shadow-lg z-50 transition-all duration-300">
          <button className="absolute top-4 right-4 text-gray-400 text-3xl" onClick={() => setMenuOpen(false)}>
            <FaTimes />
          </button>
          <ul className="text-center mt-20 space-y-6 text-gray-300 text-lg">
            <li><Link to="/" className="block py-2 hover:text-yellow-400" onClick={() => setMenuOpen(false)}>üè† Home</Link></li>
            <li><Link to="/services" className="block py-2 hover:text-yellow-400" onClick={() => setMenuOpen(false)}>‚ú® Services</Link></li>
            <li><Link to="/horoscope" className="block py-2 hover:text-yellow-400" onClick={() => setMenuOpen(false)}>üåô Horoscope</Link></li>
            <li><Link to="/calendar" className="block py-2 hover:text-yellow-400" onClick={() => setMenuOpen(false)}>üìÖ Calendar</Link></li>
            <li><Link to="/blog" className="block py-2 hover:text-yellow-400" onClick={() => setMenuOpen(false)}>üì∞ Blog</Link></li>

            {/* üîÆ Mobile Match Kundli Button */}
            <li>
              <Link to="/kundli-match">
                <button className="px-4 py-3 bg-gradient-to-r from-yellow-500 to-orange-500 text-black font-bold text-lg rounded-full shadow-lg hover:scale-110 transition-all duration-300 hover:shadow-yellow-500/50">
                  üîÆ Match Kundli
                </button>
              </Link>
            </li>

            {/* üåó Mobile Dark Mode Toggle */}
            <li className="flex justify-center">
              <button
                onClick={() => setDarkMode(!darkMode)}
                className="flex items-center px-4 py-2 bg-gray-800 dark:bg-gray-100 text-white dark:text-black rounded-full shadow-md hover:scale-110 transition-all duration-300"
              >
                {darkMode ? <FaSun className="text-yellow-400" /> : <FaMoon />}
                <span className="ml-2">{darkMode ? "Light Mode" : "Dark Mode"}</span>
              </button>
            </li>
          </ul>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
