import React from "react";
import { useNavigate } from "react-router-dom";

const astrologersData = [
  { id: 1, name: "Rachna", expertise: "Numerology", experience: 10, image: "/images/rachna.jpg" },
  { id: 2, name: "Pramod", expertise: "Numerology", experience: 8, image: "/images/pramod.jpg" },
  { id: 3, name: "Sangeetha", expertise: "Vedic", experience: 15, image: "/images/sangeetha.jpg" },
  { id: 4, name: "Amit", expertise: "Vedic Astrology", experience: 12, image: "/images/amit.jpg" },
];

const Hero = () => {
  const navigate = useNavigate();

  return (
    <div className="relative flex flex-col items-center justify-center text-center bg-gradient-to-r from-black via-gray-900 to-gray-800 text-white py-20">
      {/* Glowing Background */}
      <div className="absolute inset-0">
        <div className="absolute top-1/4 left-1/3 w-96 h-96 bg-yellow-500 opacity-20 rounded-full blur-3xl"></div>
        <div className="absolute bottom-1/4 right-1/3 w-96 h-96 bg-purple-500 opacity-20 rounded-full blur-3xl"></div>
      </div>

      {/* Main Content */}
      <h1 className="text-6xl font-extrabold animate-fadeIn">
        ðŸ”® Unlock Your <span className="text-yellow-400">Destiny</span>
      </h1>
      <p className="mt-4 text-lg animate-slideUp">Get personalized astrology guidance today.</p>

      {/* Call to Action */}
      <div className="mt-6 flex space-x-4">
        <button className="px-6 py-3 bg-yellow-500 text-black rounded-full hover:bg-yellow-600 transform hover:scale-105 transition">
          Get Started
        </button>
        <button className="px-6 py-3 border border-yellow-500 text-yellow-400 rounded-full hover:bg-yellow-500 hover:text-black transform hover:scale-105 transition">
          Learn More
        </button>
      </div>

      {/* Astrologers Section */}
      <div className="mt-16 w-full max-w-5xl px-6">
        <h2 className="text-4xl font-bold text-yellow-400">Our Top Astrologers</h2>
        <p className="text-gray-300">13000+ Best Astrologers from India for Online Consultation</p>

        {/* Centered Astrologers List */}
        <div className="flex justify-center gap-6 mt-6">
          {astrologersData.map((astro) => (
            <div
              key={astro.id}
              className="bg-white/10 backdrop-blur-lg text-white p-6 rounded-xl shadow-lg text-center w-56 transition-all duration-300 transform hover:scale-105 hover:shadow-xl"
            >
              <img
                src={astro.image}
                alt={astro.name}
                className="rounded-full mx-auto w-24 h-24 mb-2 border-2 border-yellow-400"
              />
              <h3 className="text-xl font-bold">{astro.name}</h3>
              <p className="text-gray-300">{astro.expertise}</p>
              <p className="text-gray-400">{astro.experience} years experience</p>
            </div>
          ))}
        </div>

        {/* View All Astrologers Button */}
        <button
          onClick={() => navigate("/astrologers")}
          className="mt-8 bg-yellow-500 text-black px-6 py-3 rounded-full hover:bg-yellow-600 transition"
        >
          View All Astrologers
        </button>
      </div>
    </div>
  );
};

export default Hero;
