import React from "react";
import { FaUserCircle } from "react-icons/fa"; // Import user icon

const Customers = () => {
  const testimonials = [
    { name: "John Doe", review: "Great astrology service! Highly recommended!" },
    { name: "Alice Smith", review: "I love the accurate horoscope readings!" },
    { name: "David Brown", review: "The best astrology consultation I have ever had!" },
    { name: "Sophia Johnson", review: "Superb customer support and expert advice!" },
    { name: "Michael Lee", review: "I enjoyed the tarot reading experience!" },
  ];

  return (
    <div className="py-12 bg-gray-900 text-white">
      <h2 className="text-4xl font-extrabold text-center text-yellow-400 mb-10 tracking-wide drop-shadow-lg">
        ✨ Our Happy Customers ✨
      </h2>

      {/* Scrollable Testimonials Section */}
      <div className="relative overflow-hidden">
        <div className="flex space-x-8 overflow-x-auto px-6 scrollbar-hide scroll-smooth">
          {testimonials.map((customer, index) => (
            <div
              key={index}
              className="min-w-[280px] bg-gradient-to-r from-gray-800 to-gray-700 p-6 rounded-xl shadow-xl transition-transform duration-300 hover:scale-105 hover:shadow-yellow-500/50"
            >
              {/* Customer Icon */}
              <div className="flex items-center space-x-3 mb-4">
                <FaUserCircle className="text-yellow-400 text-3xl" />
                <h3 className="text-xl font-semibold text-yellow-400">{customer.name}</h3>
              </div>

              {/* Review Text */}
              <p className="text-gray-300 italic leading-relaxed">
                " {customer.review} "
              </p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Customers;
