import React from "react";

const ZodiacWheel = () => {
  const zodiacWheel = "/zodiac-wheel.png"; // ✅ Your local image

  return (
    <div className="flex flex-col items-center justify-center py-16 bg-black text-white">
      <h2 className="text-4xl font-bold text-yellow-400 mb-6">♈ Rotating Zodiac Wheel</h2>
      <p className="text-gray-400 text-lg text-center max-w-3xl mb-8">
        Explore the cosmic alignment of zodiac signs and discover what the stars reveal about your destiny.
      </p>

      {/* Rotating Wheel */}
      <div className="relative flex items-center justify-center">
        <img 
          src={zodiacWheel} 
          alt="Zodiac Wheel" 
          className="w-[400px] h-[400px] md:w-[500px] md:h-[500px] rotate-animation"
        />
      </div>
    </div>
  );
};

export default ZodiacWheel;
