import React from "react";

const Hero = () => {
  return (
    <div
      className="relative h-screen flex flex-col items-center justify-center text-center bg-cover bg-center text-white"
      style={{ backgroundImage: "url('https://source.unsplash.com/1600x900/?stars,galaxy')" }}
    >
      <div className="absolute inset-0 bg-black bg-opacity-50"></div>
      <h1 className="text-5xl font-bold relative z-10">Unlock Your Destiny</h1>
      <p className="mt-3 text-lg relative z-10">Get personalized astrology guidance today.</p>
      <button className="mt-6 px-6 py-3 bg-yellow-500 text-black rounded-full hover:bg-yellow-600 relative z-10">
        Get Started
      </button>
    </div>
  );
};

export default Hero;
