import React from "react";

const zodiacSigns = [
  { name: "Aries", dates: "Mar 21 - Apr 19", symbol: "♈" },
  { name: "Taurus", dates: "Apr 20 - May 20", symbol: "♉" },
  { name: "Gemini", dates: "May 21 - Jun 20", symbol: "♊" },
  { name: "Cancer", dates: "Jun 21 - Jul 22", symbol: "♋" },
  { name: "Leo", dates: "Jul 23 - Aug 22", symbol: "♌" },
  { name: "Virgo", dates: "Aug 23 - Sep 22", symbol: "♍" },
  { name: "Libra", dates: "Sep 23 - Oct 22", symbol: "♎" },
  { name: "Scorpio", dates: "Oct 23 - Nov 21", symbol: "♏" },
  { name: "Sagittarius", dates: "Nov 22 - Dec 21", symbol: "♐" },
  { name: "Capricorn", dates: "Dec 22 - Jan 19", symbol: "♑" },
  { name: "Aquarius", dates: "Jan 20 - Feb 18", symbol: "♒" },
  { name: "Pisces", dates: "Feb 19 - Mar 20", symbol: "♓" },
];

const Horoscope = () => {
  return (
    <div className="min-h-screen bg-gradient-to-r from-purple-900 via-indigo-900 to-black text-white flex flex-col items-center justify-center p-10">
      <h1 className="text-5xl font-extrabold mb-6 text-yellow-400">
        🔮 What's Your Sign?
      </h1>
      <p className="text-lg max-w-3xl text-center mb-10">
        Discover your daily horoscope! Click on your zodiac sign to see what the stars have planned for you.
      </p>

      <div className="grid grid-cols-3 md:grid-cols-4 gap-6">
        {zodiacSigns.map((sign, index) => (
          <div
            key={index}
            className="relative bg-black bg-opacity-20 border border-yellow-400 rounded-lg p-6 text-center transition transform hover:scale-105 hover:bg-yellow-400 hover:text-black cursor-pointer"
          >
            <span className="text-6xl block">{sign.symbol}</span>
            <h2 className="text-2xl font-bold mt-2">{sign.name}</h2>
            <p className="text-sm">{sign.dates}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Horoscope;
