import React from "react";

const ZodiacWheel = () => {
  // Use an online image URL for the Zodiac Wheel
  const zodiacWheel = "https://upload.wikimedia.org/wikipedia/commons/thumb/e/ea/Zodiac_Wheel.svg/1200px-Zodiac_Wheel.svg.png";

  return (
    <div className="flex flex-col items-center justify-center py-16 bg-black text-white">
      <h2 className="text-4xl font-bold text-yellow-400 mb-6">â™ˆ Zodiac Wheel</h2>
      <p className="text-gray-400 text-lg text-center max-w-3xl mb-8">
        Explore the cosmic alignment of zodiac signs and discover what the stars reveal about your destiny.
      </p>
      <div className="relative flex items-center justify-center">
        <img 
          src={zodiacWheel} 
          alt="Zodiac Wheel" 
          className="w-96 h-96 md:w-[500px] md:h-[500px] transition-transform duration-500 hover:rotate-180"
        />
      </div>
    </div>
  );
};

export default ZodiacWheel;
