import React from "react";
import { motion } from "framer-motion"; // For animations

const ZodiacWheel = () => {
  // Use the local image from the public folder
  const zodiacWheel = "/zodiac-wheel.png"; // ✅ Correct path

  return (
    <motion.div 
      initial={{ opacity: 0, y: 50 }} 
      animate={{ opacity: 1, y: 0 }} 
      transition={{ duration: 1 }} 
      className="flex flex-col items-center justify-center py-16 bg-black text-white"
    >
      <h2 className="text-4xl font-bold text-yellow-400 mb-6">♈ Zodiac Wheel</h2>
      <p className="text-gray-400 text-lg text-center max-w-3xl mb-8">
        Explore the cosmic alignment of zodiac signs and discover what the stars reveal about your destiny.
      </p>
      <div className="relative flex items-center justify-center">
        <motion.img 
          src={zodiacWheel} 
          alt="Zodiac Wheel" 
          className="w-[400px] h-[400px] md:w-[500px] md:h-[500px] transition-transform duration-500 hover:rotate-180"
          whileHover={{ scale: 1.1 }}
        />
      </div>
    </motion.div>
  );
};

export default ZodiacWheel;
