import React from "react";
import { motion } from "framer-motion";

const ZodiacWheel = () => {
  const zodiacWheel = "/zodiac-wheel.png"; 

  return (
    <div className="flex flex-col items-center justify-center py-16 bg-black text-white">
      <h2 className="text-4xl font-bold text-yellow-400 mb-6">â™ˆ Rotating Zodiac Wheel</h2>
      <p className="text-gray-400 text-lg text-center max-w-3xl mb-8">
        Explore the cosmic alignment of zodiac signs and discover what the stars reveal about your destiny.
      </p>

      {/* Interactive Rotation */}
      <motion.img 
        src={zodiacWheel} 
        alt="Zodiac Wheel" 
        className="w-[400px] h-[400px] md:w-[500px] md:h-[500px]"
        animate={{ rotate: 360 }}
        transition={{ duration: 30, repeat: Infinity, ease: "linear" }}
        whileHover={{ scale: 1.1 }} // Slight zoom on hover
      />
    </div>
  );
};

export default ZodiacWheel;
