import React from "react";
import Blog from "./Blog";

const services = [
  { title: "Brihat Horoscope", image: "https://source.unsplash.com/300x200/?horoscope,astrology", description: "What will you get in 250+ pages?", link: "#" },
  { title: "Finance", image: "https://source.unsplash.com/300x200/?money,finance", description: "Are money matters a reason for worry?", link: "#" },
  { title: "Ask A Question", image: "https://source.unsplash.com/300x200/?question,help", description: "Is there any question or problem?", link: "#" },
  { title: "Career / Job", image: "https://source.unsplash.com/300x200/?career,job", description: "Worried about your career?", link: "#" },
  { title: "AstroSage Year Book", image: "https://source.unsplash.com/300x200/?book,astrology", description: "A must-have astrology guide!", link: "#" },
  { title: "Career Counselling", image: "https://source.unsplash.com/300x200/?education,study", description: "Get the right guidance for your career!", link: "#" },
];

const Home = () => {
  return (
    <div className="bg-black text-white">
      {/* Blog Section */}
      <Blog />

      {/* Astrological Services Section */}
      <div className="py-16 px-6">
        <h2 className="text-4xl font-bold text-yellow-400 text-center mb-8">✨ Astrological Services for Accurate Answers ✨</h2>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 justify-center">
          {services.map((service, index) => (
            <div key={index} className="p-4 bg-white/10 backdrop-blur-md rounded-lg shadow-lg text-center transition-all duration-300 hover:scale-105 hover:shadow-yellow-500/30">
              <img src={service.image} alt={service.title} className="rounded-lg mb-4" />
              <h3 className="text-lg font-bold text-yellow-400">{service.title}</h3>
              <p className="text-gray-300 mt-2">{service.description}</p>
              <a href={service.link} className="mt-4 inline-block px-6 py-2 bg-yellow-500 text-black font-semibold rounded-full hover:bg-yellow-600 transition duration-300">
                CHECK NOW →
              </a>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
