import React from "react";

const Blog = () => {
  return (
    <div className="py-20 bg-gradient-to-br from-black via-gray-900 to-black text-white text-center relative overflow-hidden">
      
      {/* Glowing Background Effects */}
      <div className="absolute top-0 left-1/3 w-[400px] h-[400px] bg-purple-500 opacity-20 rounded-full blur-[160px]"></div>
      <div className="absolute bottom-0 right-1/3 w-[400px] h-[400px] bg-yellow-500 opacity-20 rounded-full blur-[160px]"></div>

      {/* Blog Title */}
      <h2 className="text-5xl font-extrabold text-yellow-400 mb-8 tracking-wide">
        ðŸ“– Latest Astrology Insights
      </h2>
      <p className="text-gray-400 max-w-3xl mx-auto mb-14">
        Stay updated with the latest astrological news, cosmic events, and expert predictions.
      </p>

      {/* Blog Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-10 justify-center">
        {[1, 2, 3].map((_, index) => (
          <div 
            key={index} 
            className="relative w-80 p-6 bg-white/10 backdrop-blur-md rounded-xl shadow-lg border border-gray-700 
            transition-all duration-300 transform hover:scale-105 hover:shadow-yellow-500/50"
          >
            <div className="overflow-hidden rounded-lg">
              <img 
                src={`https://source.unsplash.com/400x250/?astrology,stars,cosmos&random=${index}`} 
                alt="Blog Post" 
                className="rounded-lg transition-transform duration-300 hover:scale-110"
              />
            </div>
            <div className="mt-5 text-center">
              <h3 className="text-xl font-semibold text-yellow-400">Astrology Insights #{index + 1}</h3>
              <p className="text-gray-300 mt-3">Explore the secrets of the universe and understand your cosmic path.</p>
              <button className="mt-5 px-6 py-3 bg-gradient-to-r from-yellow-500 to-orange-500 text-black 
              font-semibold rounded-full hover:scale-105 transition-all duration-300 hover:shadow-yellow-500/50">
                Read More â†’
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Blog;
