import React, { useState } from "react";

const servicesData = [
  { id: 1, name: "Daily Horoscope", category: "Horoscope" },
  { id: 2, name: "Weekly Horoscope", category: "Horoscope" },
  { id: 3, name: "Palmistry", category: "Readings" },
  { id: 4, name: "Tarot Reading", category: "Readings" },
  { id: 5, name: "Numerology", category: "Readings" },
  { id: 6, name: "Chat with Astrologer", category: "Consultation" },
  { id: 7, name: "Talk to Astrologer", category: "Consultation" },
  { id: 8, name: "Kundli Matching", category: "Consultation" },
];

const categories = ["All", "Horoscope", "Readings", "Consultation"];

const Services = () => {
  const [selectedCategory, setSelectedCategory] = useState("All");

  const filteredServices = servicesData.filter(
    (service) => selectedCategory === "All" || service.category === selectedCategory
  );

  return (
    <div className="py-16 bg-black text-white text-center">
      <h2 className="text-4xl font-bold text-yellow-400">ðŸ”® Our Astrology Services</h2>
      <p className="text-gray-400">Explore a variety of astrology services tailored for you.</p>

      {/* Category Filter */}
      <div className="mt-6 flex justify-center space-x-4">
        {categories.map((category) => (
          <button
            key={category}
            className={`px-6 py-2 rounded-full transition-transform duration-300 text-sm font-semibold 
              ${
                selectedCategory === category
                  ? "bg-yellow-500 text-black shadow-lg shadow-yellow-500/50 scale-105"
                  : "bg-gray-800 text-gray-300 hover:bg-yellow-500 hover:text-black hover:scale-105"
              }`}
            onClick={() => setSelectedCategory(category)}
          >
            {category}
          </button>
        ))}
      </div>

      {/* Services List */}
      <div className="mt-10 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 px-6">
        {filteredServices.map((service) => (
          <div
            key={service.id}
            className="relative bg-white/10 backdrop-blur-xl text-white p-6 rounded-xl shadow-xl text-center 
            transition-transform duration-300 hover:scale-105 hover:shadow-yellow-500/50"
          >
            <h3 className="text-lg font-semibold">{service.name}</h3>
            <p className="text-gray-400 mt-2">{service.category}</p>
            <button className="absolute bottom-4 right-4 px-4 py-2 text-sm font-semibold text-black bg-yellow-500 rounded-full 
            hover:bg-yellow-600 transition duration-300">
              Explore â†’
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Services;
