import React, { useState } from "react";

const Review = () => {
  const [name, setName] = useState("");
  const [review, setReview] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Review Submitted:", { name, review });
    setName("");
    setReview("");
  };

  return (
    <div className="flex flex-col items-center justify-center py-12 bg-black text-white">
      {/* Title */}
      <h2 className="text-3xl font-extrabold text-yellow-400 mb-6 flex items-center">
        ðŸ’¬ Write a Review
      </h2>

      {/* Review Form */}
      <form
        onSubmit={handleSubmit}
        className="bg-gray-900/40 backdrop-blur-lg p-6 rounded-lg shadow-lg w-[90%] md:w-[600px] border border-gray-700 
        transition-all duration-300 hover:shadow-yellow-500/30"
      >
        <div className="flex flex-col md:flex-row space-y-4 md:space-y-0 md:space-x-4">
          {/* Name Input */}
          <input
            type="text"
            placeholder="Your Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="w-full md:w-1/2 p-3 bg-transparent border border-gray-500 text-white rounded-lg 
            focus:ring-2 focus:ring-yellow-400 transition-all placeholder-gray-400 outline-none"
          />

          {/* Review Input */}
          <textarea
            placeholder="Your Review"
            value={review}
            onChange={(e) => setReview(e.target.value)}
            className="w-full md:w-1/2 p-3 bg-transparent border border-gray-500 text-white rounded-lg 
            focus:ring-2 focus:ring-yellow-400 transition-all placeholder-gray-400 outline-none h-24"
          />
        </div>

        {/* Submit Button */}
        <button
          type="submit"
          className="mt-6 w-full md:w-auto px-6 py-3 bg-gradient-to-r from-yellow-500 to-orange-500 text-black 
          font-bold text-lg uppercase rounded-full shadow-lg hover:shadow-yellow-500/50 hover:scale-105 
          transition-all duration-300"
        >
          ðŸš€ Submit Review
        </button>
      </form>
    </div>
  );
};

export default Review;
