import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const Navbar = () => {
  const [darkMode, setDarkMode] = useState(
    localStorage.getItem("darkMode") === "true"
  );

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
    localStorage.setItem("darkMode", darkMode);
  }, [darkMode]);

  return (
    <nav className="bg-white dark:bg-gray-900 shadow-lg fixed top-0 left-0 w-full z-50">
      <div className="max-w-7xl mx-auto px-6 flex justify-between items-center py-4">
        {/* Logo */}
        <Link to="/" className="text-3xl font-bold text-yellow-500">
          ðŸ”® AstroSphere
        </Link>

        {/* Desktop Menu */}
        <ul className="hidden md:flex space-x-6 text-gray-700 dark:text-white font-medium">
          <li><Link to="/" className="hover:text-yellow-400 transition">Home</Link></li>
          <li><Link to="/horoscope" className="hover:text-yellow-400 transition">Horoscope</Link></li>
          <li><Link to="/consult" className="hover:text-yellow-400 transition">Consult</Link></li>
          <li><Link to="/blog" className="hover:text-yellow-400 transition">Blog</Link></li>
        </ul>

        {/* Buttons */}
        <div className="flex items-center space-x-4">
          <button
            onClick={() => setDarkMode(!darkMode)}
            className="text-gray-700 dark:text-white text-lg"
          >
            {darkMode ? "ðŸŒž" : "ðŸŒ™"}
          </button>
          <button className="px-4 py-2 bg-yellow-500 text-black rounded-full hover:bg-yellow-600 transition">
            Login
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
