import React from "react";
import { useNavigate } from "react-router-dom";

const Hero = () => {
  const navigate = useNavigate();

  return (
    <div className="relative flex flex-col items-center justify-center text-center bg-black min-h-screen px-6 pt-40 pb-24">
      {/* Glowing Background Effects */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute top-1/4 left-1/3 w-[350px] h-[350px] bg-yellow-500 opacity-20 rounded-full blur-[120px]"></div>
        <div className="absolute bottom-1/4 right-1/3 w-[350px] h-[350px] bg-purple-500 opacity-20 rounded-full blur-[120px]"></div>
      </div>

      {/* Main Heading */}
      <h1 className="text-5xl md:text-6xl font-extrabold text-white tracking-wide animate-fadeIn leading-tight">
        ðŸ”® Unlock Your <span className="text-transparent bg-clip-text bg-gradient-to-r from-yellow-400 to-pink-500">Destiny</span>
      </h1>
      <p className="mt-5 text-lg md:text-xl text-gray-300 animate-slideUp max-w-2xl">
        Get personalized astrology guidance from top astrologers.
      </p>

      {/* Call to Action Buttons */}
      <div className="mt-8 flex flex-wrap justify-center gap-6">
        <button
          onClick={() => navigate("/signup")}
          className="px-8 py-3 bg-gradient-to-r from-yellow-500 to-orange-500 text-black font-semibold rounded-full shadow-lg 
          hover:scale-105 transition-transform duration-300 hover:shadow-yellow-500/50 active:scale-95"
        >
          Get Started
        </button>
        <button
          onClick={() => navigate("/about")}
          className="px-8 py-3 border border-yellow-500 text-yellow-400 font-semibold rounded-full shadow-lg 
          hover:bg-yellow-500 hover:text-black hover:scale-105 transition-transform duration-300 active:scale-95"
        >
          Learn More
        </button>
        <button
          onClick={() => navigate("/chat-astrologer")}
          className="px-8 py-3 bg-gradient-to-r from-purple-500 to-blue-600 text-white font-semibold rounded-full shadow-lg 
          hover:scale-105 transition-transform duration-300 hover:shadow-purple-500/50 active:scale-95"
        >
          Start Chat
        </button>
      </div>

      {/* Premium Astrology Consultation Section */}
      <div className="mt-40 w-full max-w-7xl px-6 text-center">
        <h2 className="text-4xl font-bold text-yellow-400">ðŸŒŸ Premium Astrology Consultation</h2>
        <p className="text-gray-400 max-w-3xl mx-auto mt-3">
          Get expert guidance from top astrologers. Choose your session type and book now!
        </p>

        {/* Consultation Options */}
        <div className="mt-14 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-10">
          {["Basic Reading", "Deep Analysis", "Life Path Consultation"].map((session, index) => (
            <div 
              key={index} 
              className="relative p-8 bg-white/10 backdrop-blur-xl border border-yellow-500 rounded-xl text-center 
              shadow-lg shadow-yellow-500/20 transition-all duration-300 transform hover:scale-105 hover:shadow-yellow-500/50"
            >
              <h3 className="text-2xl font-bold text-white">{session}</h3>
              <p className="mt-3 text-gray-400">30 min session with a top astrologer</p>
              <button className="mt-6 px-6 py-3 bg-gradient-to-r from-yellow-500 to-orange-500 text-black font-semibold rounded-full 
              hover:scale-105 transition-transform duration-300 hover:shadow-yellow-500/50 active:scale-95">
                Book Now
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Hero;
