import React, { useState } from "react";
import { Link } from "react-router-dom";
import { auth, RecaptchaVerifier, signInWithPhoneNumber } from "../firebaseConfig";
import { FaBars, FaTimes, FaMoon, FaSun, FaUser } from "react-icons/fa";

const Navbar = () => {
  const [darkMode, setDarkMode] = useState(localStorage.getItem("darkMode") === "true");
  const [menuOpen, setMenuOpen] = useState(false);
  const [showLogin, setShowLogin] = useState(false);
  const [phoneNumber, setPhoneNumber] = useState("");
  const [otp, setOtp] = useState("");
  const [confirmationResult, setConfirmationResult] = useState(null);
  const [error, setError] = useState("");

  const handleDarkMode = () => {
    setDarkMode(!darkMode);
    document.documentElement.classList.toggle("dark", !darkMode);
    localStorage.setItem("darkMode", !darkMode);
  };

  // Handle OTP Request
  const sendOtp = async () => {
    if (phoneNumber.length < 10) {
      setError("Enter a valid phone number!");
      return;
    }

    try {
      window.recaptchaVerifier = new RecaptchaVerifier(auth, "recaptcha-container", { size: "invisible" });
      const confirmation = await signInWithPhoneNumber(auth, `+91${phoneNumber}`, window.recaptchaVerifier);
      setConfirmationResult(confirmation);
      setError("OTP sent! Check your phone.");
    } catch (err) {
      setError("Failed to send OTP. Try again.");
    }
  };

  // Handle OTP Verification
  const verifyOtp = async () => {
    if (!otp) {
      setError("Enter OTP!");
      return;
    }

    try {
      await confirmationResult.confirm(otp);
      setError("Login successful!");
      setShowLogin(false);
    } catch (err) {
      setError("Invalid OTP. Try again.");
    }
  };

  return (
    <nav className="fixed top-0 left-0 w-full z-50 bg-white/30 dark:bg-gray-900/40 backdrop-blur-lg shadow-lg border-b border-white/20 dark:border-gray-800/50">
      <div className="max-w-7xl mx-auto px-6 flex justify-between items-center py-4">
        <Link to="/" className="text-3xl font-bold text-yellow-500 transition hover:scale-105">
          üîÆ AstroSphere
        </Link>

        {/* Dark Mode & Login */}
        <div className="flex items-center space-x-4">
          <button onClick={handleDarkMode} className="text-gray-700 dark:text-white text-lg transition">
            {darkMode ? <FaSun /> : <FaMoon />}
          </button>
          <button onClick={() => setShowLogin(true)} className="px-4 py-2 bg-yellow-500 text-black rounded-full hover:bg-yellow-600 transition flex items-center">
            <FaUser className="mr-2" /> Login
          </button>
        </div>
      </div>

      {/* Login Modal */}
      {showLogin && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white dark:bg-gray-900 p-6 rounded-lg shadow-lg w-96 text-center">
            <h2 className="text-2xl font-bold text-yellow-500">üîê Login with OTP</h2>
            <p className="text-gray-600 dark:text-gray-300 mt-2">Enter your phone number</p>

            <input
              type="tel"
              className="w-full p-2 mt-4 border border-gray-400 rounded-lg dark:bg-gray-800 dark:text-white"
              placeholder="Enter your phone number"
              value={phoneNumber}
              onChange={(e) => setPhoneNumber(e.target.value)}
            />

            <div id="recaptcha-container"></div>

            {confirmationResult ? (
              <>
                <p className="text-gray-600 dark:text-gray-300 mt-2">Enter OTP</p>
                <input
                  type="text"
                  className="w-full p-2 mt-4 border border-gray-400 rounded-lg dark:bg-gray-800 dark:text-white"
                  placeholder="Enter OTP"
                  value={otp}
                  onChange={(e) => setOtp(e.target.value)}
                />
                <button onClick={verifyOtp} className="mt-4 px-6 py-2 bg-green-500 text-white rounded-full hover:bg-green-600 transition">
                  Verify OTP
                </button>
              </>
            ) : (
              <button onClick={sendOtp} className="mt-4 px-6 py-2 bg-yellow-500 text-black rounded-full hover:bg-yellow-600 transition">
                Send OTP
              </button>
            )}

            {error && <p className="mt-2 text-red-500">{error}</p>}

            <button onClick={() => setShowLogin(false)} className="mt-4 text-gray-600 dark:text-gray-300 hover:text-red-500 transition">
              Close
            </button>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
