import React, { useState } from "react";

const KundliMatch = () => {
  const [userData, setUserData] = useState({
    name: "",
    day: "1",
    month: "Jan",
    year: "1990",
    hour: "1",
    minute: "0",
    place: "",
  });

  const handleChange = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("ðŸ”® Kundli Matched Successfully!");
  };

  return (
    <div className="bg-gray-900 text-white py-10 px-6 rounded-lg shadow-lg max-w-3xl mx-auto my-10">
      <h2 className="text-3xl font-bold text-center text-yellow-400 mb-6">
        ðŸ”® Match Your Kundli
      </h2>

      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Name Input */}
        <input
          type="text"
          name="name"
          placeholder="Enter Name"
          value={userData.name}
          onChange={handleChange}
          className="w-full p-3 rounded-lg bg-gray-800 border border-gray-600 focus:outline-none focus:ring-2 focus:ring-yellow-500"
          required
        />

        {/* Birth Details */}
        <div className="grid grid-cols-2 gap-4">
          <select name="day" value={userData.day} onChange={handleChange} className="p-3 rounded-lg bg-gray-800 border border-gray-600">
            {Array.from({ length: 31 }, (_, i) => i + 1).map((d) => (
              <option key={d} value={d}>{d}</option>
            ))}
          </select>

          <select name="month" value={userData.month} onChange={handleChange} className="p-3 rounded-lg bg-gray-800 border border-gray-600">
            {["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"].map((m) => (
              <option key={m} value={m}>{m}</option>
            ))}
          </select>

          <select name="year" value={userData.year} onChange={handleChange} className="p-3 rounded-lg bg-gray-800 border border-gray-600">
            {Array.from({ length: 100 }, (_, i) => 2024 - i).map((y) => (
              <option key={y} value={y}>{y}</option>
            ))}
          </select>
        </div>

        {/* Birth Time */}
        <div className="grid grid-cols-2 gap-4">
          <select name="hour" value={userData.hour} onChange={handleChange} className="p-3 rounded-lg bg-gray-800 border border-gray-600">
            {Array.from({ length: 24 }, (_, i) => i).map((h) => (
              <option key={h} value={h}>{h}</option>
            ))}
          </select>

          <select name="minute" value={userData.minute} onChange={handleChange} className="p-3 rounded-lg bg-gray-800 border border-gray-600">
            {Array.from({ length: 60 }, (_, i) => i).map((m) => (
              <option key={m} value={m}>{m}</option>
            ))}
          </select>
        </div>

        {/* Birth Place */}
        <input
          type="text"
          name="place"
          placeholder="Enter Birth Place"
          value={userData.place}
          onChange={handleChange}
          className="w-full p-3 rounded-lg bg-gray-800 border border-gray-600 focus:outline-none focus:ring-2 focus:ring-yellow-500"
          required
        />

        {/* ðŸ”¥ Match Kundli Button */}
        <button
          type="submit"
          className="w-full mt-4 bg-gradient-to-r from-yellow-400 to-orange-500 text-black font-bold py-3 rounded-lg shadow-lg hover:scale-105 transition-all duration-300 hover:shadow-yellow-500/50"
        >
          ðŸ”® Match Kundli
        </button>
      </form>
    </div>
  );
};

export default KundliMatch;
